import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Rating = () => {
  const [reports, setReports] = useState([]);
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [ratingCounts, setRatingCounts] = useState([0, 0, 0, 0, 0]); // For ratings 1-5
  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  // Fetch reports on component mount
  useEffect(() => {
    axios.get('/api/reports', { headers })
      .then(res => {
        setReports(res.data);
        calculateRatingDistribution(res.data);
      })
      .catch(err => alert('Failed to fetch reports'));
  }, []);

  // Function to handle rating input change
  const handleRatingChange = (reportId, field, value) => {
    setRatings(prev => ({
      ...prev,
      [reportId]: {
        ...prev[reportId],
        [field]: value
      }
    }));
  };

  // Function to submit a rating
  const handleAddRating = async (reportId) => {
    const ratingData = ratings[reportId] || { rating: 0, comments: '' };
    const ratingValue = parseInt(ratingData.rating);
    if (!ratingValue || ratingValue < 1 || ratingValue > 5) {
      alert('Rating must be between 1 and 5');
      return;
    }
    try {
      await axios.post('/api/ratings', { report_id: reportId, ...ratingData }, { headers });
      alert('Rating added');
      // Optionally, refresh reports or update rating counts
      // For simplicity, assume ratings are added and update counts
      updateRatingCounts(ratingValue);
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to add rating');
    }
  };

  // Handle search
  const handleSearch = async () => {
    try {
      const res = await axios.get(`/api/search/reports?query=${searchQuery}`, { headers });
      setReports(res.data);
      calculateRatingDistribution(res.data);
    } catch (err) {
      alert('Search failed');
    }
  };

  // Calculate the distribution of ratings for all reports
  const calculateRatingDistribution = (reportsData) => {
    const counts = [0, 0, 0, 0, 0]; // For ratings 1-5
    reportsData.forEach(report => {
      const rating = parseInt(ratings[report.id]?.rating);
      if (rating >= 1 && rating <= 5) {
        counts[rating - 1]++;
      }
    });
    setRatingCounts(counts);
  };

  // Update counts when a new rating is added
  const updateRatingCounts = (rating) => {
    setRatingCounts(prev => {
      const newCounts = [...prev];
      newCounts[rating - 1]++;
      return newCounts;
    });
  };

  // Prepare data for the cumulative frequency curve
  const getCumulativeData = () => {
    const totalRatings = ratingCounts.reduce((a, b) => a + b, 0);
    const cumulativeCounts = [];
    let cumulativeSum = 0;
    for (let i = 0; i < ratingCounts.length; i++) {
      cumulativeSum += ratingCounts[i];
      cumulativeCounts.push(cumulativeSum);
    }
    const labels = [1, 2, 3, 4, 5];
    const data = {
      labels,
      datasets: [
        {
          label: 'Cumulative Frequency',
          data: cumulativeCounts,
          fill: false,
          backgroundColor: 'rgba(75,192,192,0.4)',
          borderColor: 'rgba(75,192,192,1)',
          tension: 0.1
        }
      ],
    };
    return data;
  };

  return (
    <div className="container mt-5">
      <h3>Rate Reports</h3>
      <input
        type="text"
        placeholder="Search reports..."
        className="form-control mb-3"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch} className="btn btn-secondary mb-3">Search</button>
      
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Lecturer</th>
            <th>Topic</th>
            <th>Rating (1-5)</th>
            <th>Comments</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.lecturer_name}</td>
              <td>{report.topic_taught}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  max="5"
                  className="form-control"
                  value={ratings[report.id]?.rating || ''}
                  onChange={(e) => handleRatingChange(report.id, 'rating', e.target.value)}
                />
              </td>
              <td>
                <textarea
                  className="form-control"
                  value={ratings[report.id]?.comments || ''}
                  onChange={(e) => handleRatingChange(report.id, 'comments', e.target.value)}
                />
              </td>
              <td>
                <button onClick={() => handleAddRating(report.id)} className="btn btn-sm btn-primary">Rate</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {/* Cumulative Frequency Curve */}
      <h4 className="mt-5">Cumulative Frequency Curve of Ratings</h4>
      <Bar data={getCumulativeData()} options={{
        responsive: true,
        scales: {
          y: {
            beginAtZero: true,
            title: { display: true, text: 'Cumulative Frequency' }
          },
          x: {
            title: { display: true, text: 'Ratings' }
          }
        }
      }} />
    </div>
  );
};

export default Rating;
