import React, { useState, useEffect } from 'react';

const Lectures = () => {
  const [lectures, setLectures] = useState([]);
  const [name, setName] = useState('');
  const [modules, setModules] = useState('');
  const [phone, setPhone] = useState('');
  const [picture, setPicture] = useState(null);
  const [imagePreview, setImagePreview] = useState('');

  // Load existing lectures (simulate or fetch from backend)
  useEffect(() => {
    // For demo, initialize with dummy data
    setLectures([
      {
        id: 1,
        name: 'lerato',
        modules: ['Math', 'Physics'],
        phone: '1234567890',
        picture: '/images/hghvy'
      },
      {
        id: 2,
        name: 'reitu',
        modules: ['Chemistry'],
        phone: '0987654321',
        picture: '/images/jane.jpg'
      }
    ]);
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setPicture(file);
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleAddLecture = () => {
    if (!name || !modules || !phone || !picture) {
      alert('Please fill all fields and select an image.');
      return;
    }

    const newLecture = {
      id: Date.now(),
      name,
      modules: modules.split(',').map(m => m.trim()), // allow comma-separated modules
      phone,
      picture: imagePreview // in real app, upload to server and get URL
    };

    setLectures([...lectures, newLecture]);

    // Reset form
    setName('');
    setModules('');
    setPhone('');
    setPicture(null);
    setImagePreview('');
  };

  return (
    <div>
      <h3>Lectures</h3>
      
      {/* Table of existing lectures */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Modules</th>
            <th>Phone</th>
            <th>Picture</th>
          </tr>
        </thead>
        <tbody>
          {lectures.map(lecture => (
            <tr key={lecture.id}>
              <td>{lecture.id}</td>
              <td>{lecture.name}</td>
              <td>{lecture.modules.join(', ')}</td>
              <td>{lecture.phone}</td>
              <td>
                <img
                  src={lecture.picture}
                  alt={lecture.name}
                  style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add new lecture form */}
      <h4>Add New Lecture</h4>
      <div className="mb-3">
        <label>Name:</label>
        <input
          type="text"
          className="form-control"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label>Modules (comma separated):</label>
        <input
          type="text"
          className="form-control"
          value={modules}
          onChange={(e) => setModules(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label>Phone Number:</label>
        <input
          type="text"
          className="form-control"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label>Picture:</label>
        <input
          type="file"
          accept="image/*"
          className="form-control"
          onChange={handleImageChange}
        />
        {imagePreview && (
          <img
            src={imagePreview}
            alt="Preview"
            style={{ width: '100px', marginTop: '10px' }}
          />
        )}
      </div>
      <button className="btn btn-primary" onClick={handleAddLecture}>Add Lecture</button>
    </div>
  );
};

export default Lectures;
