import React, { useState } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import StudentMonitoring from './Monitoring';
import StudentRating from './Rating';

const StudentDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <>
      {/* Hover Styles */}
      <style>
        {`
          .nav-link:hover {
            background-color: #495057;
            color: #ffc107 !important;
            border-radius: 4px;
          }

          .btn:hover {
            background-color: #e0e0e0;
            color: #3f0071 !important;
          }

          button:hover {
            opacity: 0.9;
          }
        `}
      </style>

      <div className="d-flex min-vh-100" style={{ fontFamily: 'Arial, sans-serif' }}>

        {/* Sidebar */}
        <nav
          className={`bg-dark text-white p-3 ${sidebarOpen ? 'w-25' : 'w-15'} transition-all`}
          style={{
            minHeight: '100vh',
            width: sidebarOpen ? '250px' : '200px',
            transition: 'width 0.3s',
            backgroundColor: '#343a40',
          }}
        >
          <button
            className="btn btn-light mb-3"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? '<<' : '>>'}
          </button>

          <ul className="nav flex-column">
            <li className="nav-item">
              <Link to="/student/dashboard/monitoring" className="nav-link text-white">
                Monitoring
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/student/dashboard/rating" className="nav-link text-white">
                Rating
              </Link>
            </li>
          </ul>
        </nav>

        {/* Main Content */}
        <main className="flex-grow-1 p-4 bg-light" style={{ padding: 0 }}>

          {/* Header */}
          <div
            style={{
              backgroundColor: '#3f0071',
              color: 'white',
              padding: '15px 30px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}
          >
            <h2 style={{ margin: 0 }}>Dashboard</h2>
            <div>
              <button
                style={{
                  marginRight: '10px',
                  backgroundColor: '#fff',
                  color: '#3f0071',
                  border: 'none',
                  padding: '8px 15px',
                  borderRadius: '5px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                }}
              >
                Home
              </button>
              <button
                style={{
                  backgroundColor: '#fff',
                  color: '#3f0071',
                  border: 'none',
                  padding: '8px 15px',
                  borderRadius: '5px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                }}
              >
                Add
              </button>
            </div>
          </div>

          <div style={{ padding: '30px' }}>
            <Routes>
              <Route path="monitoring" element={<StudentMonitoring />} />
              <Route path="rating" element={<StudentRating />} />
            </Routes>
          </div>
        </main>
      </div>
    </>
  );
};

export default StudentDashboard;
