import React, { useState, useEffect } from 'react';
// import axios from 'axios'; // comment out axios since no API
import { Line } from 'react-chartjs-2'; // for graph
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Rating = () => {
  const [reports, setReports] = useState([]);
  const [ratings, setRatings] = useState({});
  const [editing, setEditing] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Dummy data for reports
  const dummyReports = [
    { id: 1, lecturer_name: 'John Doe', topic_taught: 'Math' },
    { id: 2, lecturer_name: 'Jane Smith', topic_taught: 'Physics' },
    { id: 3, lecturer_name: 'Alan Turing', topic_taught: 'Computer Science' },
  ];

  // Instead of fetching, initialize reports
  useEffect(() => {
    setReports(dummyReports);
  }, []);

  const handleRatingChange = (reportId, field, value) => {
    setRatings({
      ...ratings,
      [reportId]: { ...ratings[reportId], [field]: value },
    });
  };

  const handleAddRating = (reportId) => {
    const ratingData = ratings[reportId] || { rating: 3, comments: '' };
    if (!ratingData.rating || ratingData.rating < 1 || ratingData.rating > 5) {
      alert('Rating must be between 1 and 5');
      return;
    }
    // Instead of API, just update local state
    setRatings((prev) => ({
      ...prev,
      [reportId]: ratingData,
    }));
    alert('Rating added (simulated)');
  };

  const handleEdit = (reportId) => {
    setEditing(reportId);
  };

  const handleSaveEdit = (reportId) => {
    // No API call, just update local ratings
    alert('Rating updated (simulated)');
    setEditing(null);
  };

  const handleDelete = (reportId) => {
    if (!window.confirm('Are you sure you want to delete this rating?')) return;
    setRatings((prev) => {
      const newRatings = { ...prev };
      delete newRatings[reportId];
      return newRatings;
    });
    alert('Rating deleted (simulated)');
  };

  const handleSearch = () => {
    // No real search, filter dummy data
    const filtered = dummyReports.filter(
      (r) =>
        r.lecturer_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        r.topic_taught.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setReports(filtered);
  };

  // Prepare data for the graph: average ratings per report
  const ratingValues = Object.values(ratings).map((r) => r.rating || 3);
  const labels = reports.map((r) => `Report ${r.id}`);

  const data = {
    labels: labels,
    datasets: [
      {
        label: 'Ratings',
        data: ratingValues,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="container mt-5">
      <h3>Rate Reports</h3>
      <input
        type="text"
        placeholder="Search reports..."
        className="form-control mb-3"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch} className="btn btn-secondary mb-3">
        Search
      </button>

      {/* Line graph of ratings */}
      <div style={{ maxWidth: '600px', marginBottom: '40px' }}>
        <h5 style={{ textDecoration: 'underline' }}>Ratings Trend</h5>
        <Line data={data} />
      </div>

      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Lecturer</th>
            <th>Topic</th>
            <th>Rating (1-5)</th>
            <th>Comments</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((report) => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.lecturer_name}</td>
              <td>{report.topic_taught}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  max="5"
                  className="form-control"
                  value={ratings[report.id]?.rating || ''}
                  onChange={(e) =>
                    handleRatingChange(report.id, 'rating', Number(e.target.value))
                  }
                  disabled={editing !== report.id && ratings[report.id]?.rating}
                />
              </td>
              <td>
                <textarea
                  className="form-control"
                  value={ratings[report.id]?.comments || ''}
                  onChange={(e) =>
                    handleRatingChange(report.id, 'comments', e.target.value)
                  }
                  disabled={editing !== report.id && ratings[report.id]?.comments}
                />
              </td>
              <td>
                {editing === report.id ? (
                  <button
                    onClick={() => handleSaveEdit(report.id)}
                    style={{
                      backgroundColor: '#28a745',
                      color: '#fff',
                      border: 'none',
                      padding: '6px 12px',
                      borderRadius: '4px',
                      fontSize: '14px',
                      marginBottom: '5px',
                      marginRight: '5px',
                      cursor: 'pointer'
                    }}
                  >
                    Save
                  </button>
                ) : (
                  <>
                    <button
                      onClick={() => handleAddRating(report.id)}
                      className="btn btn-sm btn-primary"
                      style={{ marginRight: '5px' }}
                    >
                      Rate
                    </button>
                    <button
                      onClick={() => handleEdit(report.id)}
                      style={{
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        padding: '6px 12px',
                        borderRadius: '4px',
                        fontSize: '14px',
                        marginRight: '5px',
                        cursor: 'pointer'
                      }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(report.id)}
                      style={{
                        backgroundColor: '#dc3545',
                        color: '#fff',
                        border: 'none',
                        padding: '6px 12px',
                        borderRadius: '4px',
                        fontSize: '14px',
                        cursor: 'pointer'
                      }}
                    >
                      Delete
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Rating;
