import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'bootstrap/dist/css/bootstrap.min.css';

// Chart.js setup (required for react-chartjs-2 v3+)
import {
  Chart as ChartJS,
  LineElement,
  Title,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale
} from 'chart.js';

ChartJS.register(LineElement, Title, Tooltip, Legend, CategoryScale, LinearScale);

const Rating = () => {
  const [reports, setReports] = useState([]);
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  // State for new ratings (additional ratings)
  const [additionalRatings, setAdditionalRatings] = useState({
    lectureContentQuality: {},
    studentEngagement: {},
    lecturerPerformance: {}
  });

  // Load reports on mount
  useEffect(() => {
    axios
      .get('/api/reports', { headers })
      .then(res => setReports(res.data))
      .catch(err => alert('Failed to fetch reports'));
  }, []);

  // Handle individual report rating change
  const handleRatingChange = (reportId, field, value) => {
    setRatings(prev => ({
      ...prev,
      [reportId]: {
        ...prev[reportId],
        [field]: value
      }
    }));
  };

  // Handle additional ratings change
  const handleAdditionalRatingChange = (category, reportId, value) => {
    setAdditionalRatings(prev => ({
      ...prev,
      [category]: {
        ...prev[category],
        [reportId]: value
      }
    }));
  };

  // Submit rating for a report
  const handleAddRating = async (reportId) => {
    const ratingData = ratings[reportId] || { rating: 0, comments: '' };
    if (!ratingData.rating || ratingData.rating < 1 || ratingData.rating > 5) {
      alert('Rating must be between 1 and 5');
      return;
    }
    try {
      await axios.post('/api/ratings', { report_id: reportId, ...ratingData }, { headers });
      alert('Rating added');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to add rating');
    }
  };

  // Handle search
  const handleSearch = async () => {
    try {
      const res = await axios.get(`/api/search/reports?query=${searchQuery}`, { headers });
      setReports(res.data);
    } catch (err) {
      alert('Search failed');
    }
  };

  // Prepare data for the line chart
  const chartData = {
    labels: reports.map(r => `Report ${r.id}`),
    datasets: [
      {
        label: 'Overall Rating',
        data: reports.map(r => parseFloat(r.average_rating || 0)),
        borderColor: 'blue',
        fill: false,
        tension: 0.1
      },
      {
        label: 'Lecture Content Quality',
        data: reports.map(r => parseFloat(additionalRatings.lectureContentQuality[r.id] || 0)),
        borderColor: 'green',
        fill: false,
        tension: 0.1
      },
      {
        label: 'Student Engagement',
        data: reports.map(r => parseFloat(additionalRatings.studentEngagement[r.id] || 0)),
        borderColor: 'orange',
        fill: false,
        tension: 0.1
      },
      {
        label: 'Lecturer Performance',
        data: reports.map(r => parseFloat(additionalRatings.lecturerPerformance[r.id] || 0)),
        borderColor: 'red',
        fill: false,
        tension: 0.1
      }
    ]
  };

  return (
    <div className="container mt-5">
      <h3>Rate Reports</h3>
      
      {/* Search input */}
      <input
        type="text"
        placeholder="Search reports..."
        className="form-control mb-3"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      
      {/* Reports table */}
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Lecturer</th>
            <th>Topic</th>
            <th>Rating (1-5)</th>
            <th>Comments</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((report) => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.lecturer_name}</td>
              <td>{report.topic_taught}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  max="5"
                  className="form-control"
                  value={ratings[report.id]?.rating || ''}
                  onChange={(e) => handleRatingChange(report.id, 'rating', e.target.value)}
                />
              </td>
              <td>
                <textarea
                  className="form-control"
                  value={ratings[report.id]?.comments || ''}
                  onChange={(e) => handleRatingChange(report.id, 'comments', e.target.value)}
                />
              </td>
              <td>
                <button onClick={() => handleAddRating(report.id)} className="btn btn-sm btn-primary">Rate</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Additional Ratings Inputs for each report */}
      <h4>Additional Ratings</h4>
      {reports.map((report) => (
        <div key={report.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          <h5>Report {report.id}</h5>
          {/* Lecture Content Quality */}
          <div className="mb-2">
            <label>Lecture Content Quality (1-5): </label>
            <input
              type="number"
              min="1"
              max="5"
              className="form-control"
              value={additionalRatings.lectureContentQuality[report.id] || ''}
              onChange={(e) => handleAdditionalRatingChange('lectureContentQuality', report.id, e.target.value)}
            />
          </div>
          {/* Student Engagement */}
          <div className="mb-2">
            <label>Student Engagement (1-5): </label>
            <input
              type="number"
              min="1"
              max="5"
              className="form-control"
              value={additionalRatings.studentEngagement[report.id] || ''}
              onChange={(e) => handleAdditionalRatingChange('studentEngagement', report.id, e.target.value)}
            />
          </div>
          {/* Lecturer Performance */}
          <div className="mb-2">
            <label>Lecturer Performance (1-5): </label>
            <input
              type="number"
              min="1"
              max="5"
              className="form-control"
              value={additionalRatings.lecturerPerformance[report.id] || ''}
              onChange={(e) => handleAdditionalRatingChange('lecturerPerformance', report.id, e.target.value)}
            />
          </div>
        </div>
      ))}

      {/* Button to generate the line graph */}
      <h4>Ratings Line Graph</h4>
      <Line data={chartData} />

    </div>
  );
};

export default Rating;
