import React, { useState } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import StudentMonitoring from './Monitoring';
import StudentRating from './Rating';

const StudentDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false); // Mode toggle state

  // Toggle mode handler
  const toggleMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  // Determine background color based on mode
  const backgroundColor = isDarkMode ? '#3B5170' : '#ffefd5';

  return (
    <>
      {/* Hover Styles for links and buttons */}
      <style>
        {`
          .nav-link:hover {
            background-color: #b0c4de;
            text-decoration: underline;
          }
          .btn:hover {
            background-color: #b0c4de;
            text-decoration: underline;
          }
        `}
      </style>

      <div
        style={{
          display: 'flex',
          minHeight: '100vh',
          fontFamily: 'Arial, sans-serif',
          backgroundColor: backgroundColor,
        }}
      >
        {/* Sidebar */}
        <nav
          style={{
            minHeight: '100vh',
            width: sidebarOpen ? '250px' : '200px',
            transition: 'width 0.3s',
            backgroundColor: '#b0c4de',
          }}
        >
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            style={{
              width: '100%',
              padding: '8px',
              border: 'none',
              borderRadius: '4px',
              backgroundColor: '#fff',
              color: '#007bff',
              fontWeight: 'bold',
              cursor: 'pointer',
              marginBottom: '10px',
            }}
          >
            {sidebarOpen ? '<<' : '>>'}
          </button>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            <li style={{ marginBottom: '15px' }}>
              <Link
                to="/student/dashboard/monitoring"
                className="nav-link"
              >
                Monitoring
              </Link>
            </li>
            <li style={{ marginBottom: '15px' }}>
              <Link
                to="/student/dashboard/rating"
                className="nav-link"
              >
                Rating
              </Link>
            </li>
          </ul>
        </nav>

        {/* Main Content */}
        <main
          style={{
            flex: 1,
            backgroundColor: backgroundColor,
            padding: '20px',
            position: 'relative',
            marginLeft: sidebarOpen ? '250px' : '200px',
          }}
        >
          {/* Logout Button in top right corner (green) */}
          <button
            style={{
              position: 'absolute',
              top: '10px',
              right: '20px',
              backgroundColor: 'green',
              color: '#fff',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              textDecoration: 'underline',
              fontWeight: 'bold',
            }}
            onClick={() => {
              localStorage.removeItem('token');
              window.location.href = '/login';
            }}
          >
            Logout
          </button>

          {/* Mode Toggle Button */}
          <button
            style={{
              position: 'absolute',
              top: '10px',
              right: '120px',
              backgroundColor: '#3B5170',
              color: '#fff',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold',
            }}
            onClick={toggleMode}
          >
            Toggle Mode
          </button>

          {/* Header */}
          <div
            style={{
              backgroundColor: '#b0c4de',
              padding: '15px',
              borderRadius: '4px',
              marginBottom: '20px',
              border: '1px solid #ffefd5',
            }}
          >
            <h2 style={{ margin: 0, textDecoration: 'underline' }}>Dashboard</h2>
          </div>

          {/* Routes */}
          <div>
            <Routes>
              <Route path="monitoring" element={<StudentMonitoring />} />
              <Route path="rating" element={<StudentRating />} />
            </Routes>
          </div>
        </main>
      </div>
    </>
  );
};

export default StudentDashboard;
