import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import 'bootstrap/dist/css/bootstrap.min.css';

// Import Chart.js modules
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const Reports = () => {
  // State variables
  const [form, setForm] = useState({
    faculty_name: 'Faculty of Information Communication Technology',
    class_id: '',
    week: '',
    date: '',
    course_id: '',
    course_code: '',
    lecturer_name: '',
    actual_students_present: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });
  
  const [errors, setErrors] = useState({});
  const [courses, setCourses] = useState([]);
  const [classes, setClasses] = useState([]);
  const [reports, setReports] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedTopic, setEditedTopic] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [totalStudents, setTotalStudents] = useState(0);
  
  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  // Fetch data on component mount
  useEffect(() => {
    axios.get('/api/courses', { headers }).then(res => setCourses(res.data));
    axios.get('/api/classes', { headers }).then(res => setClasses(res.data));
    axios.get('/api/reports', { headers }).then(res => setReports(res.data));
  }, []);

  // Validation function
  const validateForm = () => {
    const newErrors = {};
    if (!form.faculty_name) newErrors.faculty_name = 'Required';
    if (!form.class_id) newErrors.class_id = 'Required';
    if (!form.week || form.week < 1) newErrors.week = 'Must be a positive number';
    if (!form.date) newErrors.date = 'Required';
    if (!form.course_id) newErrors.course_id = 'Required';
    if (!form.course_code) newErrors.course_code = 'Required';
    if (!form.lecturer_name) newErrors.lecturer_name = 'Required';
    if (form.actual_students_present === '' || form.actual_students_present < 0) newErrors.actual_students_present = 'Must be non-negative';
    if (!form.venue) newErrors.venue = 'Required';
    if (!form.scheduled_time) newErrors.scheduled_time = 'Required';
    if (!form.topic_taught) newErrors.topic_taught = 'Required';
    if (!form.learning_outcomes) newErrors.learning_outcomes = 'Required';
    if (!form.recommendations) newErrors.recommendations = 'Required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle change in form inputs
  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  // Handle course selection change
  const handleCourseChange = (e) => {
    const courseId = e.target.value;
    setForm({ ...form, course_id: courseId });
    const selectedCourse = courses.find(c => c.id == courseId);
    if (selectedCourse) setTotalStudents(selectedCourse.total_registered_students);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    try {
      await axios.post('/api/reports', form, { headers });
      alert('Report submitted');
      // Refresh reports
      const res = await axios.get('/api/reports', { headers });
      setReports(res.data);
      // Reset form
      setForm({
        faculty_name: 'Faculty of Information Communication Technology',
        class_id: '',
        week: '',
        date: '',
        course_id: '',
        course_code: '',
        lecturer_name: '',
        actual_students_present: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: ''
      });
      setTotalStudents(0);
    } catch (err) {
      alert(err.response?.data?.message || 'Submission failed');
    }
  };

  // Search reports
  const handleSearch = async () => {
    try {
      const res = await axios.get(`/api/search/reports?query=${searchQuery}`, { headers });
      setReports(res.data);
    } catch (err) {
      alert('Search failed');
    }
  };

  // Edit report
  const handleEdit = (report) => {
    setEditingId(report.id);
    setEditedTopic(report.topic_taught);
  };

  // Save edited report
  const handleSaveEdit = async (reportId) => {
    try {
      await axios.put(`/api/reports/${reportId}`, { topic_taught: editedTopic }, { headers });
      alert('Report updated');
      setEditingId(null);
      setEditedTopic('');
      const updated = await axios.get('/api/reports', { headers });
      setReports(updated.data);
    } catch (err) {
      alert('Failed to update report');
    }
  };

  // Delete report
  const handleDelete = async (reportId) => {
    if (!window.confirm('Are you sure you want to delete this report?')) return;
    try {
      await axios.delete(`/api/reports/${reportId}`, { headers });
      alert('Report deleted');
      setReports(reports.filter((r) => r.id !== reportId));
    } catch (err) {
      alert('Failed to delete report');
    }
  };

  // Prepare data for pie chart
  const pieData = {
    labels: ['Actual Students Present', 'Total Registered Students'],
    datasets: [
      {
        label: 'Students',
        data: [
          reports.reduce((sum, r) => sum + (parseInt(r.actual_students_present) || 0), 0),
          reports.reduce((sum, r) => sum + (r.course ? r.course.total_registered_students : 0), 0)
        ],
        backgroundColor: ['#36A2EB', '#FF6384'],
        hoverBackgroundColor: ['#36A2EB', '#FF6384']
      }
    ]
  };

  return (
    <div className="container mt-5">
      {/* Data Entry Form */}
      <h3>Data Entry (Lecturer Reporting Form)</h3>
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="row mb-3">
          <div className="col">
            <label className="form-label">Faculty Name</label>
            <input
              type="text"
              className="form-control"
              name="faculty_name"
              value={form.faculty_name}
              readOnly
            />
          </div>
          <div className="col">
            <label className="form-label">Class Name</label>
            <select
              className={`form-select ${errors.class_id ? 'is-invalid' : ''}`}
              name="class_id"
              value={form.class_id}
              onChange={handleChange}
            >
              <option value="">Select Class</option>
              {classes.map((cls) => (
                <option key={cls.id} value={cls.id}>{cls.class_name}</option>
              ))}
            </select>
            {errors.class_id && <div className="invalid-feedback">{errors.class_id}</div>}
          </div>
        </div>

        <div className="row mb-3">
          <div className="col">
            <label className="form-label">Week of Reporting</label>
            <input
              type="number"
              className={`form-control ${errors.week ? 'is-invalid' : ''}`}
              name="week"
              value={form.week}
              onChange={handleChange}
            />
            {errors.week && <div className="invalid-feedback">{errors.week}</div>}
          </div>
          <div className="col">
            <label className="form-label">Date of Lecture</label>
            <input
              type="date"
              className={`form-control ${errors.date ? 'is-invalid' : ''}`}
              name="date"
              value={form.date}
              onChange={handleChange}
            />
            {errors.date && <div className="invalid-feedback">{errors.date}</div>}
          </div>
        </div>

        <div className="row mb-3">
          <div className="col">
            <label className="form-label">Course Name</label>
            <select
              className={`form-select ${errors.course_id ? 'is-invalid' : ''}`}
              name="course_id"
              value={form.course_id}
              onChange={handleCourseChange}
            >
              <option value="">Select Course</option>
              {courses.map((course) => (
                <option key={course.id} value={course.id}>{course.name}</option>
              ))}
            </select>
            {errors.course_id && <div className="invalid-feedback">{errors.course_id}</div>}
          </div>
          <div className="col">
            <label className="form-label">Course Code</label>
            <input
              type="text"
              className={`form-control ${errors.course_code ? 'is-invalid' : ''}`}
              name="course_code"
              value={form.course_code}
              onChange={handleChange}
            />
            {errors.course_code && <div className="invalid-feedback">{errors.course_code}</div>}
          </div>
        </div>

        <div className="mb-3">
          <label className="form-label">Lecturer's Name</label>
          <input
            type="text"
            className={`form-control ${errors.lecturer_name ? 'is-invalid' : ''}`}
            name="lecturer_name"
            value={form.lecturer_name}
            onChange={handleChange}
          />
          {errors.lecturer_name && <div className="invalid-feedback">{errors.lecturer_name}</div>}
        </div>

        <div className="row mb-3">
          <div className="col">
            <label className="form-label">Actual Number of Students Present</label>
            <input
              type="number"
              className={`form-control ${errors.actual_students_present ? 'is-invalid' : ''}`}
              name="actual_students_present"
              value={form.actual_students_present}
              onChange={handleChange}
            />
            {errors.actual_students_present && <div className="invalid-feedback">{errors.actual_students_present}</div>}
          </div>
          <div className="col">
            <label className="form-label">Total Registered Students</label>
            <input
              type="number"
              className="form-control"
              value={totalStudents}
              readOnly
            />
          </div>
        </div>

        <div className="mb-3">
          <label className="form-label">Venue</label>
          <input
            type="text"
            className={`form-control ${errors.venue ? 'is-invalid' : ''}`}
            name="venue"
            value={form.venue}
            onChange={handleChange}
          />
          {errors.venue && <div className="invalid-feedback">{errors.venue}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Scheduled Time</label>
          <input
            type="time"
            className={`form-control ${errors.scheduled_time ? 'is-invalid' : ''}`}
            name="scheduled_time"
            value={form.scheduled_time}
            onChange={handleChange}
          />
          {errors.scheduled_time && <div className="invalid-feedback">{errors.scheduled_time}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Topic Taught</label>
          <input
            type="text"
            className={`form-control ${errors.topic_taught ? 'is-invalid' : ''}`}
            name="topic_taught"
            value={form.topic_taught}
            onChange={handleChange}
          />
          {errors.topic_taught && <div className="invalid-feedback">{errors.topic_taught}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Learning Outcomes</label>
          <textarea
            className={`form-control ${errors.learning_outcomes ? 'is-invalid' : ''}`}
            name="learning_outcomes"
            value={form.learning_outcomes}
            onChange={handleChange}
          />
          {errors.learning_outcomes && <div className="invalid-feedback">{errors.learning_outcomes}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Recommendations</label>
          <textarea
            className={`form-control ${errors.recommendations ? 'is-invalid' : ''}`}
            name="recommendations"
            value={form.recommendations}
            onChange={handleChange}
          />
          {errors.recommendations && <div className="invalid-feedback">{errors.recommendations}</div>}
        </div>

        <button type="submit" className="btn btn-primary">Submit Report</button>
      </form>

      {/* Search Reports */}
      <h3 className="mt-5">My Reports</h3>
      <div className="d-flex mb-3">
        <input
          type="text"
          placeholder="Search reports..."
          className="form-control me-2"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch} className="btn btn-secondary">Search</button>
      </div>

      {/* Reports Table */}
      <table className="table table-striped mb-4">
        <thead>
          <tr>
            <th>ID</th>
            <th>Week</th>
            <th>Date</th>
            <th>Lecturer</th>
            <th>Topic</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((report) => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.week}</td>
              <td>{report.date}</td>
              <td>{report.lecturer_name}</td>
              <td>
                {editingId === report.id ? (
                  <input
                    className="form-control"
                    value={editedTopic}
                    onChange={(e) => setEditedTopic(e.target.value)}
                  />
                ) : (
                  report.topic_taught
                )}
              </td>
              <td>
                <a
                  href={`/api/reports/${report.id}/excel`}
                  className="btn btn-sm btn-info me-2"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Download
                </a>
                {editingId === report.id ? (
                  <button
                    onClick={() => handleSaveEdit(report.id)}
                    className="btn btn-success btn-sm"
                  >
                    Save
                  </button>
                ) : (
                  <>
                    <button
                      onClick={() => handleEdit(report)}
                      className="btn btn-primary btn-sm me-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(report.id)}
                      className="btn btn-danger btn-sm"
                    >
                      Delete
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pie Chart Visualization */}
      <h3 className="mt-5">Summary Pie Chart</h3>
      <div className="d-flex justify-content-center">
        <div style={{ maxWidth: '500px', width: '100%' }}>
          <Pie data={pieData} />
        </div>
      </div>
    </div>
  );
};

export default Reports;
