import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Lectures = () => {
  const [lectures, setLectures] = useState([]);
  const [name, setName] = useState('');
  const [modules, setModules] = useState('');
  const [phone, setPhone] = useState('');
  const [picture, setPicture] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [showForm, setShowForm] = useState(false);

  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  // Load existing lectures from your database (simulate with dummy data or fetch from server)
  useEffect(() => {
    // Example: fetch lecturers from your database
    // For demo, initialize with dummy data
    setLectures([
      {
        id: 1,
        name: 'lerato',
        modules: ['Math', 'Physics'],
        phone: '1234567890',
        picture: '/images/hghvy'
      },
      {
        id: 2,
        name: 'reitu',
        modules: ['Chemistry'],
        phone: '0987654321',
        picture: '/images/jane.jpg'
      }
    ]);
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setPicture(file);
    setImagePreview(URL.createObjectURL(file));
  };

  const handleAddLecture = async (e) => {
    e.preventDefault();
    if (!name || !modules || !phone || !picture) {
      alert('Please fill all fields and select an image.');
      return;
    }

    // Upload image to server if needed. For demo, use local preview URL
    // In real case, upload image to server and get URL
    const pictureUrl = imagePreview; 

    // Save to your database - assuming your backend supports adding to users table
    try {
      await axios.post(
        '/api/users', // Make sure your backend supports creating new users
        {
          username: name,
          password: 'defaultpassword', // Or generate/set as needed
          role: 'lecturer', // assuming role 'lecturer' for lecturers
          faculty: 'faculty_name', // set your faculty if needed
          modules: modules,
          phone: phone,
          picture: pictureUrl
        },
        { headers }
      );

      // After successful save, fetch latest lecturers or update local state
      // Fetch all lecturers again
      const res = await axios.get('/api/users?role=lecturer', { headers });
      setLectures(res.data);

      // Reset form
      setName('');
      setModules('');
      setPhone('');
      setPicture(null);
      setImagePreview('');
      setShowForm(false);
    } catch (err) {
      alert('Error saving lecturer: ' + err.message);
    }
  };

  return (
    <div>
      <h2>Lectures</h2>
      <button onClick={() => setShowForm(true)}>Add Lecture</button>
      {/* List existing lectures */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Modules</th>
            <th>Phone</th>
            <th>Picture</th>
          </tr>
        </thead>
        <tbody>
          {lectures.map((lecture) => (
            <tr key={lecture.id}>
              <td>{lecture.id}</td>
              <td>{lecture.username || lecture.name}</td>
              <td>{lecture.modules ? lecture.modules.join(', ') : ''}</td>
              <td>{lecture.phone}</td>
              <td>
                <img
                  src={lecture.picture}
                  alt={lecture.name}
                  style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Lecture Form Modal */}
      {showForm && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: '#fff',
              padding: 20,
              borderRadius: 8,
              width: '400px',
            }}
          >
            <h3>Add New Lecturer</h3>
            <form onSubmit={handleAddLecture}>
              <div className="mb-3">
                <label>Name:</label>
                <input
                  type="text"
                  className="form-control"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label>Modules (comma separated):</label>
                <input
                  type="text"
                  className="form-control"
                  value={modules}
                  onChange={(e) => setModules(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label>Phone:</label>
                <input
                  type="text"
                  className="form-control"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label>Picture:</label>
                <input
                  type="file"
                  accept="image/*"
                  className="form-control"
                  onChange={handleImageChange}
                />
                {imagePreview && (
                  <img
                    src={imagePreview}
                    alt="Preview"
                    style={{ width: '100px', marginTop: '10px' }}
                  />
                )}
              </div>
              <button type="submit" className="btn btn-primary">
                Save Lecturer
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => setShowForm(false)}
                style={{ marginLeft: '10px' }}
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Lectures;
