// Lectures.js
import React, { useState, useEffect } from 'react';

const Lectures = () => {
  const [lectures, setLectures] = useState([]);
  const [name, setName] = useState('');
  const [modules, setModules] = useState('');
  const [phone, setPhone] = useState('');
  const [picture, setPicture] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [addOpen, setAddOpen] = useState(false);

  // Load existing lectures (simulate or fetch from backend)
  useEffect(() => {
    // For demo, initialize with dummy data
    setLectures([
      {
        id: 1,
        name: 'Lerato',
        modules: ['Math', 'Physics'],
        phone: '1234567890',
        picture: '/images/hghvy',
      },
      {
        id: 2,
        name: 'Reitumetse',
        modules: ['Chemistry', 'Biology'],
        phone: '9876543210',
        picture: '/images/jygn',
      },
    ]);
  }, []);

  // Function to handle adding new lecture
  const addLecture = (e) => {
    e.preventDefault();
    const newLecture = {
      id: lectures.length + 1,
      name,
      modules,
      phone,
      picture,
    };
    setLectures([...lectures, newLecture]);
    setName('');
    setModules('');
    setPhone('');
    setPicture(null);
    setImagePreview('');
  };

  // Function to handle image upload
  const handleImageChange = (event) => {
    const image = event.target.files[0];
    setPicture(image);
    setImagePreview(URL.createObjectURL(image));
  };

  return (
    <div>
      <h2>Lectures</h2>
      <button onClick={() => setAddOpen(true)}>Add Lecture</button>
      <button onClick={() => console.log(lectures)}>Log Lectures</button>

      {/* Add Lecture Modal */}
      {addOpen && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0,0,0,0.7)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div
            style={{
              background: 'white',
              padding: 20,
              borderRadius: 5,
              width: 500,
              height: 300,
            }}
          >
            <h2>Add Lecture</h2>
            <form onSubmit={addLecture}>
              <label>
                Name:
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </label>
              <br />
              <label>
                Modules:
                <input
                  type="text"
                  value={modules}
                  onChange={(e) => setModules(e.target.value)}
                />
              </label>
              <br />
              <label>
                Phone:
                <input
                  type="text"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                />
              </label>
              <br />
              <label>
                Picture:
                <input
                  type="file"
                  onChange={handleImageChange}
                  accept=".jpg,.jpeg,.png"
                />
                {imagePreview && (
                  <img
                    src={imagePreview}
                    alt="Preview"
                    style={{ width: 100, height: 100 }}
                  />
                )}
              </label>
              <br />
              <button type="submit">Add</button>
            </form>
            <button onClick={() => setAddOpen(false)}>Cancel</button>
          </div>
        </div>
      )}

      {/* Lecture Table */}
      <h2>Lecture Table</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Modules</th>
            <th>Phone</th>
            <th>Picture</th>
          </tr>
        </thead>
        <tbody>
          {lectures.map((lecture) => (
            <tr key={lecture.id}>
              <td>{lecture.id}</td>
              <td>{lecture.name}</td>
              <td>{lecture.modules.join(', ')}</td>
              <td>{lecture.phone}</td>
              <td>
                <img src={lecture.picture} alt={lecture.name} style={{ width: 50, height: 50 }} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Lectures;
