import React, { useState } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import LecturerClasses from './Classes';
import LecturerReports from './Reports';
import LecturerMonitoring from './Monitoring';
import LecturerRating from './Rating';

const LecturerDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="d-flex min-vh-100">
      {/* Sidebar */}
      <nav className={`bg-dark text-white p-3 ${sidebarOpen ? 'w-25' : 'w-15'} transition-all`} style={{ minHeight: '100vh', backgroundColor: '#007bff' }}>
        <button
          className="btn btn-light mb-3"
          onClick={() => setSidebarOpen(!sidebarOpen)}
          style={{ backgroundColor: '#ffffff', color: '#007bff', border: 'none', padding: '5px 10px', borderRadius: '5px' }}
        >
          {sidebarOpen ? '<<' : '>>'}
        </button>
        <ul className="nav flex-column" style={{ listStyle: 'none', padding: 0 }}>
          <li className="nav-item" style={{ marginBottom: '15px' }}>
            <Link to="/lecturer/dashboard/classes" className="nav-link text-white" style={{ color: '#ffffff', textDecoration: 'none', fontSize: '16px', padding: '5px 0' }}>Classes</Link>
          </li>
          <li className="nav-item" style={{ marginBottom: '15px' }}>
            <Link to="/lecturer/dashboard/reports" className="nav-link text-white" style={{ color: '#ffffff', textDecoration: 'none', fontSize: '16px', padding: '5px 0' }}>Reports</Link>
          </li>
          <li className="nav-item" style={{ marginBottom: '15px' }}>
            <Link to="/lecturer/dashboard/monitoring" className="nav-link text-white" style={{ color: '#ffffff', textDecoration: 'none', fontSize: '16px', padding: '5px 0' }}>Monitoring</Link>
          </li>
          <li className="nav-item">
            <Link to="/lecturer/dashboard/rating" className="nav-link text-white" style={{ color: '#ffffff', textDecoration: 'none', fontSize: '16px', padding: '5px 0' }}>Rating</Link>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <main className="flex-grow-1 p-4 bg-light" style={{ marginLeft: '200px', padding: '20px', backgroundColor: '#ffffff', minHeight: '100vh', borderLeft: '1px solid #dee2e6' }}>
        <header style={{ backgroundColor: '#103b68ff', color: '#ffffff', padding: '15px', marginBottom: '20px', borderRadius: '5px' }}>
          <h2>Lecturer Dashboard</h2>
        </header>
        <Routes>
          <Route path="classes" element={<LecturerClasses />} />
          <Route path="reports" element={<LecturerReports />} />
          <Route path="monitoring" element={<LecturerMonitoring />} />
          <Route path="rating" element={<LecturerRating />} />
        </Routes>
      </main>
    </div>
  );
};

export default LecturerDashboard;
