import React, { useState } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import PRLCourses from './Courses';
import PRLReports from './Reports';
import PRLMonitoring from './Monitoring';
import PRLRating from './Rating';
import PRLClasses from './Classes';

const PRLDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <>
      {/* Custom styles for hover effects */}
      <style>
        {`
          .nav-link:hover {
            background-color: #b0c4de;
            text-decoration: underline;
          }
          .btn:hover {
            background-color: #b0c4de;
            text-decoration: underline;
          }
        `}
      </style>

      <div style={{ display: 'flex', minHeight: '100vh', fontFamily: 'Arial, sans-serif', backgroundColor: '#ffefd5' }}>
        {/* Sidebar */}
        <nav
          style={{
            minHeight: '100vh',
            width: sidebarOpen ? '250px' : '200px',
            transition: 'width 0.3s',
            backgroundColor: '#b0c4de'
          }}
        >
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            style={{
              width: '100%',
              padding: '8px',
              border: 'none',
              borderRadius: '4px',
              backgroundColor: '#fff',
              color: '#007bff',
              fontWeight: 'bold',
              cursor: 'pointer',
              marginBottom: '10px'
            }}
          >
            {sidebarOpen ? '<<' : '>>'}
          </button>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {[
              { to: '/prl/dashboard/courses', label: 'Courses' },
              { to: '/prl/dashboard/reports', label: 'Reports' },
              { to: '/prl/dashboard/monitoring', label: 'Monitoring' },
              { to: '/prl/dashboard/rating', label: 'Rating' },
              { to: '/prl/dashboard/classes', label: 'Classes' },
            ].map((item, index) => (
              <li key={index} style={{ marginBottom: '15px' }}>
                <Link
                  to={item.to}
                  className="nav-link"
                >
                  {item.label}
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        {/* Main Content */}
        <main
          style={{
            flex: 1,
            backgroundColor: '#ffefd5',
            padding: '20px',
            position: 'relative',
            marginLeft: sidebarOpen ? '250px' : '200px'
          }}
        >
          {/* Logout Button in top right corner (green) */}
          <button
            style={{
              position: 'absolute',
              top: '10px',
              right: '20px',
              backgroundColor: 'green',
              color: '#fff',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              textDecoration: 'underline'
            }}
            onClick={() => {
              // Logout logic
              localStorage.removeItem('token');
              window.location.href = '/login';
            }}
          >
            Logout
          </button>

          {/* Header */}
          <div
            style={{
              backgroundColor: '#b0c4de',
              padding: '15px',
              borderRadius: '4px',
              marginBottom: '20px',
              border: '1px solid #ffefd5'
            }}
          >
            <h2 style={{ margin: 0, textDecoration: 'underline' }}>PRL Dashboard</h2>
          </div>

          {/* Routes */}
          <div>
            <Routes>
              <Route path="courses" element={<PRLCourses />} />
              <Route path="reports" element={<PRLReports />} />
              <Route path="monitoring" element={<PRLMonitoring />} />
              <Route path="rating" element={<PRLRating />} />
              <Route path="classes" element={<PRLClasses />} />
            </Routes>
          </div>
        </main>
      </div>
    </>
  );
};

export default PRLDashboard;
